
let input: unknown;
input = "Hello"; // Chuỗi
input = 42;      // Số
input = true;    // Boolean
// Phải kiểm tra kiểu trước khi sử dụng
if (typeof input === "string") {
  console.log(input.toUpperCase()); // HELLO
}
if (typeof input === "number") {
  console.log(input + 10); // 52
}
